  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="h1" components={components} props={{"id":"fonts"}}>{`Fonts`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"defining-the-font-stack"}}>{`Defining the font stack`}</MDXTag>
<MDXTag name="p" components={components}>{`The font stack can be configured as part of the theme. The default font stack is applied to the `}<MDXTag name="inlineCode" components={components} parentName="p">{`body`}</MDXTag>{` and the Open Sans font stack is applied to `}<MDXTag name="inlineCode" components={components} parentName="p">{`pre`}</MDXTag>{` and `}<MDXTag name="inlineCode" components={components} parentName="p">{`code`}</MDXTag>{` tags. The default values are:`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-js","metastring":null}}>{`export const typography = {
  fontFamily: {
    default: "'Open Sans', sans-serif;",
    nunito: 'Nunito',
  }
};
`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"usage-in-code"}}>{`Usage in code`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-js","metastring":null}}>{`import theme from 'plurall-ui/themes'

const baseStyles = css\`
  font-family: \${theme.typography.fontFamily.default};
  font-size: \${theme.typography.text.normal};
\`
`}</MDXTag></MDXTag>
           </MDXTag>
  }
}
